mapper.select:
	select r.*, p.channel_name, b.channel_name as busi_channel_name from csr_pay_mer_relation r 
	    left join csr_payment_channel p on p.channel_code = r.channel_code 
	  
      AND r.channel_code = #{channelCode,jdbcType=VARCHAR} 
    
      left join csr_busi_channel b on b.channel_code = r.busi_channel_code
   
     where 1=1
    
      AND r.mall_id = #{mallId,jdbcType=TINYINT}
    
    
      AND r.mall_name like concat('%',#{mallName,jdbcType=VARCHAR},'%') 
    
    
      and p.channel_name like concat('%',#{channelName,jdbcType=VARCHAR},'%')
    
    
      AND r.busi_channel_code =  #{busiChannelCode,jdbcType=VARCHAR} 
    
     order by created_date desc

	select * from csr_pay_mer_relation where 1=1
	  
      AND channel_code = #{channelCode,jdbcType=VARCHAR} 
    
    
      AND busi_channel_code = #{busiChannelCode,jdbcType=VARCHAR}
    
    
      AND mall_id = #{mallId,jdbcType=TINYINT}

	select * from csr_pay_mer_relation where 1=1
	  
      AND channel_code = #{channelCode,jdbcType=VARCHAR} 
    
    
      AND merchant_id = #{merchantId,jdbcType=VARCHAR}
    
    
      AND app_id = #{appId,jdbcType=TINYINT}
